import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';
import 'package:marketplace/pages/ProductPage.dart';

void main() {
  runApp(HomeGoodsApp());
}

class HomeGoodsApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      debugShowCheckedModeBanner: false,
      home: HomePage(),
    );
  }
}

class HomePage extends StatelessWidget {
  final ScrollController _scrollController = ScrollController(); // ScrollController pour la page

  // URLs des images
  final List<String> imageUrls = [
    'data:image/jpeg;base64,', 
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQcySDpmJR45_Z8V5_XkQhHgGOt8NNter9r3R6IkAUEWxbwJMGSLor8e0rso-mwdeEfQP0&usqp=CAU',
    'data:image/jpeg;base64,',
    'https://ae01.alicdn.com/kf/Sb6a0ab7f2ae8443f818d06e765e91bfaQ.jpg_640x640Q90.jpg_.webp',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR1gRaWsBdfCYronjpHXRp1oIQ5KQF4bkmbiw&s',
    'data:image/jpeg;base64,',
  ];

  final List<String> largeImageUrls = [
    'data:image/jpeg;base64,', // Remplacez par l'URL de vos images
    'data:image/jpeg;base64,',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT-FIgfpjfCdRPfNM8vwaqP6N9e_UOoM-z2iA&s',
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: SingleChildScrollView(
        controller: _scrollController,
        child: Padding(
          padding: const EdgeInsets.all(16.0),
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: [
              // Flèche de retour et titre Home Goods alignés
              Padding(
                padding: const EdgeInsets.only(top: 16.0, bottom: 24.0),
                child: Row(
                  children: [
                    IconButton(
                      icon: Icon(Icons.arrow_back),
                      onPressed: () {
                        // Fonction de retour en haut
                        _scrollController.animateTo(
                          0.0,
                          duration: Duration(milliseconds: 300),
                          curve: Curves.easeInOut,
                        );
                      },
                    ),
                    Expanded(
                      child: Center(
                        child: Text(
                          'Home Goods',
                          style: TextStyle(fontSize: 28, fontWeight: FontWeight.bold),
                        ),
                      ),
                    ),
                  ],
                ),
              ),

              // Utilisation d'un Wrap pour les 6 conteneurs
              Wrap(
                spacing: 16.0, // Espacement horizontal entre les conteneurs
                runSpacing: 16.0, // Espacement vertical entre les lignes
                children: List.generate(6, (index) {
                  return Container(
                    width: (MediaQuery.of(context).size.width - 50) / 2, // 2 éléments par ligne
                    decoration: BoxDecoration(
                      borderRadius: BorderRadius.circular(8.0),
                      color: Colors.grey[200],
                    ),
                    child: Column(
                      mainAxisAlignment: MainAxisAlignment.start, // Aligner les éléments en haut
                      crossAxisAlignment: CrossAxisAlignment.start, // Aligner à gauche
                      children: [
                        Padding(
                          padding: const EdgeInsets.all(8.0),
                          child: Text(
                            'Titre ${index + 1}',
                            style: TextStyle(
                              fontSize: 16,
                              fontWeight: FontWeight.bold,
                              color: Colors.black,
                            ),
                          ),
                        ),
                        ClipRRect(
                          borderRadius: BorderRadius.circular(8.0),
                          child: Image.network(
                            imageUrls[index], // Chargez l'image depuis le réseau
                            width: double.infinity, // L'image prend toute la largeur disponible
                            height: 100, // Taille de l'image
                            fit: BoxFit.cover,
                            errorBuilder: (context, error, stackTrace) {
                              return Center(child: Text('Image non disponible')); // Gestion des erreurs
                            },
                          ),
                        ),
                      ],
                    ),
                  );
                }),
              ),

              SizedBox(height: 24),

              // Section Popular Brands avec bouton "View All"
              Row(
                mainAxisAlignment: MainAxisAlignment.spaceBetween,
                children: [
                  Text(
                    'Popular Brands',
                    style: TextStyle(fontSize: 20, fontWeight: FontWeight.bold),
                  ),
                  TextButton(
                    onPressed: () {
              // Utilisez Navigator.push pour naviguer vers la page HomePage
              Navigator.push(
                context,
                MaterialPageRoute(builder: (context) => ProductPage()), // Remplacez HomePage() par votre propre page si nécessaire
              );
            },
                    child: Text('View all >'),
                  ),
                ],
              ),
              SizedBox(height: 24),

              // Trois grandes images avec texte au centre
              SingleChildScrollView(
                scrollDirection: Axis.horizontal, // Défilement horizontal
                child: Row(
                  children: [
                    LargeImageWithText(
                      imagePath: largeImageUrls[0], 
                      text: 'KOPPARBJÖRK',
                    ),
                    LargeImageWithText(
                      imagePath: largeImageUrls[1], 
                      text: 'IVA',
                    ),
                    LargeImageWithText(
                      imagePath: largeImageUrls[2], 
                      text: 'Texte sur l\'image 3',
                    ),
                  ],
                ),
              ),
            ],
          ),
        ),
      ),
      bottomNavigationBar: CupertinoTabBar(
        items: const [
          BottomNavigationBarItem(icon: Icon(Icons.home)),
          BottomNavigationBarItem(icon: Icon(Icons.credit_card)),
          BottomNavigationBarItem(icon: Icon(Icons.shopping_cart)),
          BottomNavigationBarItem(icon: Icon(Icons.favorite)),
          BottomNavigationBarItem(icon: Icon(Icons.settings)),
        ],
      ),
    );
  }
}


// Widget pour les grandes images avec texte au centre
class LargeImageWithText extends StatelessWidget {
  final String imagePath;
  final String text;

  const LargeImageWithText({
    Key? key,
    required this.imagePath,
    required this.text,
  }) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Padding(
      padding: const EdgeInsets.only(right: 16.0), // Espacement entre les images
      child: Stack(
        children: [
          ClipRRect(
            borderRadius: BorderRadius.circular(8.0),
            child: Image.network(
              imagePath,
              width: 400, // Largeur des images
              height: 200,
              fit: BoxFit.cover,
              errorBuilder: (context, error, stackTrace) {
                return Center(child: Text('Image non disponible')); // Gestion des erreurs
              },
            ),
          ),
          Positioned.fill(
            child: Center(
              child: Text(
                text,
                style: TextStyle(
                  fontSize: 18,
                  fontWeight: FontWeight.bold,
                  color: Colors.white,
                  
                ),
                textAlign: TextAlign.center,
              ),
            ),
          ),
        ],
      ),
    );
  }
}
